package basic;

public class QuotientAndRemainder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=100,j=50;
		int Quotient=i/j;
		int Remainder=i%j;
		System.out.println("Quotient values:"+Quotient);
		System.out.println("Remainder values:"+Remainder);
		

	}

}
